import { Link, useParams } from "react-router-dom";

import EventItem from "../components/EventItem";

export default function EventsDetailPage() {

    const event = useParams();

    return (
        <EventItem event={event} />
    );
}

export function loader({request, params}) {

    const id = params.eventId; //Esse params é o que está no path do App.js, O params serve para pegar os parametros da URL

    const response = fetch('http://localhost:8080/events/' + id) //Isso serve para dar load no dado atrelado ao evento clicado, o qual tem seu próprio id
    
    return response;
}
